import React from "react";
import deski_ash from "../assets/deski_ash.svg";
import { Link, useLocation } from "react-router-dom";
import bell from "../assets/bell.svg";
import avatar from "../assets/avatar.jpg";
import { Avatar, Menu, Popover, Position, Dialog, CrossIcon, MoreIcon, TextInput } from "evergreen-ui";
import dropbox from "../assets/dropbox.png";

function Header() {
    const [isShown, setIsShown] = React.useState(false);
    const [isShown1, setIsShown1] = React.useState(false);
    // const [isShown2, setIsShown2] = React.useState(false);
    const [isShown3, setIsShown3] = React.useState(false);

    const location = useLocation();
    const [selectedtab, setSelectedTab] = React.useState(0);

    const disabled = {
        marginTop: "5px",
    };
    const profile_menu = {
        height: "30px",
    };

    React.useEffect(() => {
        if (location.pathname === "/dashboard") {
            setSelectedTab(0);
        }

        if (location.pathname === "/works") {
            setSelectedTab(1);
        }

        if (location.pathname === "/filters") {
            setSelectedTab(2);
        }

        if (location.pathname === "/people") {
            setSelectedTab(3);
        }

        if (location.pathname === "/create") {
            setSelectedTab(4);
        }
    });

    return (
        <div>
            <header>
                <Link to="/workplace">
                    <img src={deski_ash} alt="" height="60" width="110" />
                </Link>
                <div className="wrk_nav">
                    <Link to="/dashboard" className={selectedtab === 0 ? "wrknav_links_selected" : "wrknav_links"}>
                        Dashboard
                    </Link>
                    <Link to="/works" className={selectedtab === 1 ? "wrknav_links_selected" : "wrknav_links"}>
                        works
                    </Link>
                    <Link to="/filters" className={selectedtab === 2 ? "wrknav_links_selected" : "wrknav_links"}>
                        Filters
                    </Link>
                    <Link to="/people" className={selectedtab === 3 ? "wrknav_links_selected" : "wrknav_links"}>
                        People
                    </Link>
                    <Link to="/create" className={selectedtab === 4 ? "wrknav_links_selected" : "wrknav_links"}>
                        Create
                    </Link>

                    <div className="header_right">
                        <Popover
                            minWidth={240}
                            marginTop={100}
                            position={Position.BOTTOM_RIGHT}
                            content={
                                <Menu>
                                    <Menu.Group>
                                        <Menu.Item className="disabled" style={disabled} disabled>
                                            <p>deski.app</p>
                                        </Menu.Item>
                                        <Link to="/account" className="link">
                                            <Menu.Item className="profile_menu">
                                                <p>Account</p>
                                            </Menu.Item>
                                        </Link>
                                        <Link to="/upgrade" className="link">
                                            <Menu.Item className="profile_menu">
                                                <p>Upgrade</p>
                                            </Menu.Item>
                                        </Link>
                                        <Menu.Item className="profile_menu">
                                            <p>Contact sales</p>
                                        </Menu.Item>
                                        <Menu.Item className="profile_menu" onClick={() => setIsShown(true)}>
                                            <p>Connected accounts</p>
                                        </Menu.Item>
                                    </Menu.Group>
                                    <Menu.Divider />
                                    <Menu.Group>
                                        <Menu.Item style={profile_menu} className="profile_menu">
                                            <p>Logout</p>
                                        </Menu.Item>
                                    </Menu.Group>
                                </Menu>
                            }
                        >
                            <div className="profile_div">
                                <Avatar className="profile" src={avatar} size={34} />
                            </div>
                        </Popover>
                    </div>
                    <div className="bell">
                        <img src={bell} alt="" height="20" width="16" className="bell" />
                    </div>
                </div>
                <Dialog
                    margin="none"
                    padding="none"
                    topOffset={70}
                    width={570}
                    // minHeigh5Content={400}
                    onCloseComplete={() => setIsShown(false)}
                    preventBodyScrolling
                    hasHeader={false}
                    isShown={isShown}
                    hasFooter={false}
                >
                    <div className="conncted_acnts">
                        <CrossIcon float="right" marginTop={5} cursor="pointer" onClick={() => setIsShown(false)} />
                        <h4>Connected accounts</h4>
                        <Popover
                            position={Position.BOTTOM_RIGHT}
                            minWidth={50}
                            content={
                                <Menu>
                                    <Menu.Group>
                                        <Menu.Item
                                            onSelect={() => {
                                                setIsShown3(true);
                                                setIsShown(false);
                                            }}
                                        >
                                            <p className="menucontent">Rename</p>
                                        </Menu.Item>
                                        <Menu.Item>
                                            <p className="menucontent">Connect</p>
                                        </Menu.Item>
                                        <Menu.Item
                                            intent="danger"
                                            onSelect={() => {
                                                setIsShown1(true);
                                                setIsShown(false);
                                            }}
                                        >
                                            <p className="menucontentred">Delete</p>
                                        </Menu.Item>
                                    </Menu.Group>
                                </Menu>
                            }
                        >
                            <MoreIcon
                                float="right"
                                color="#cecece"
                                size={13}
                                cursor="pointer"
                                marginTop={20}
                                marginRight={35}
                            />
                        </Popover>

                        <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                        <div className="dropbox_">
                            <h4>Dropbox </h4>
                            <p>User : hexcode</p>
                        </div>
                        <p>Add accounts</p>
                        <div className="drops">
                            <div>
                                <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                                <h3>Dropbox</h3>
                                <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                                <h3>Dropbox</h3>
                            </div>
                            <div>
                                <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                                <h3>Dropbox</h3>
                                <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                                <h3>Dropbox</h3>
                            </div>
                            <div>
                                <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                                <h3>Dropbox</h3>
                                <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                                <h3>Dropbox</h3>
                            </div>
                            <div>
                                <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                                <h3>Dropbox</h3>
                                <img src={dropbox} at="" height="34" width="40" className="dropbox" />
                                <h3>Dropbox</h3>
                            </div>
                        </div>
                    </div>
                </Dialog>
                <Dialog
                    topOffset={235}
                    width={400}
                    minHeightContent={160}
                    onCloseComplete={() => setIsShown1(false)}
                    preventBodyScrolling
                    hasHeader={false}
                    isShown={isShown1}
                    hasFooter={false}
                >
                    <div className="dlt_workspace">
                        <h4>Are you sure you want to delete this workspace ? </h4>
                        <p>Recently deleted workspaces can be restored from trash.</p>

                        <div>
                            <button className="cancel_btn cncl" onClick={() => setIsShown1(false)}>
                                Cancel
                            </button>
                            <button className="dlt_btn" onClick={() => setIsShown1(false)}>
                                Delete workspace
                            </button>
                        </div>
                    </div>
                </Dialog>
                <Dialog
                    margin="none"
                    padding="none"
                    topOffset={220}
                    width={465}
                    minHeightContent={205}
                    onCloseComplete={() => setIsShown3(false)}
                    preventBodyScrolling
                    hasHeader={false}
                    isShown={isShown3}
                    hasFooter={false}
                >
                    <div className="share_workspace">
                        <h4>Change App Development to</h4>
                        <p>Change your space name to another</p>
                        <TextInput
                            className="new_name"
                            borderRadius="none"
                            width={400}
                            height={25}
                            placeholder="Enter new name"
                        />
                        <div>
                            <button className="cancel_btn" onClick={() => setIsShown3(false)}>
                                Cancel
                            </button>
                            <button className="add_btn" onClick={() => setIsShown3(false)}>
                                Save
                            </button>
                        </div>
                    </div>
                </Dialog>
            </header>
        </div>
    );
}

export default Header;
