import React from "react";
import "./Profile.css";
import trash from "../assets/trash.svg";
import star from "../assets/star.svg";
import workspace from "../assets/workspace.svg";
import ash_user from "../assets/ash_user.svg";
import collaborators from "../assets/collaborators.svg";
import { Link, useLocation } from "react-router-dom";

function Profile() {
    const location = useLocation();
    const [selectedtab, setSelectedTab] = React.useState(0);

    React.useEffect(() => {
        // alert(location.pathname)
        if (location.pathname === "/account") {
            setSelectedTab(0);
        }

        if (location.pathname === "/workspaces") {
            setSelectedTab(1);
        }

        if (location.pathname === "/collaborators") {
            setSelectedTab(2);
        }

        if (location.pathname === "/referrals") {
            setSelectedTab(3);
        }

        if (location.pathname === "/profile_trash") {
            setSelectedTab(4);
        }
    });
    return (
        <div>
            <div className="profile_sidebar">
                <p className="title">SETTINGS</p>
                <Link to="/account" className={selectedtab === 0 ? "profile_links_selected" : "profile_links"}>
                    <div className="rows">
                        <img src={ash_user} alt="" height="13" width="13" />
                        <p className="content">Account overview</p>
                    </div>
                </Link>
                <Link to="/workspaces" className={selectedtab === 0 ? "profile_links_selected" : "profile_links"}>
                    <div className="rows">
                        <img src={workspace} alt="" height="13" width="13" />
                        <p className="content">Workspaces</p>
                    </div>
                </Link>
                <Link to="/collaborators" className={selectedtab === 0 ? "profile_links_selected" : "profile_links"}>
                    <div className="rows">
                        <img src={collaborators} alt="" height="13" width="13" />
                        <p className="content">Collaborators</p>
                    </div>
                </Link>
                <Link to="/referrals" className={selectedtab === 0 ? "profile_links_selected" : "profile_links"}>
                    <div className="rows">
                        <img src={star} alt="" height="13" width="13" />
                        <p className="content">Referrals and Credit</p>
                    </div>
                </Link>
                <Link to="/profile_trash" className={selectedtab === 0 ? "profile_links_selected" : "profile_links"}>
                    <div className="rows_">
                        <img src={trash} alt="" height="10" width="10" />
                        <p className="content">Trash</p>
                    </div>
                </Link>
            </div>
        </div>
    );
}

export default Profile;
